import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)
import {isLogin, logOut} from "@/red/authMethods";

import {saveToken, loadToken} from "@/red/authMethods";

export default new Vuex.Store({
    state: {token: ''},
    getters: {
        IsLogin: state => isLogin(state.token)
    },
    mutations: {},
    actions: {
        setToken(state, token) {
            state.token = token
            saveToken(token)
        },
        LogOut(state) {
            this.state.token = ''
            saveToken('')
        }
    },
    modules: {}
})
